package practice1;

public class Problem04 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 0;
		for (int i = 1; i <= 10; i++) {
			for (int j = 1; j <= 10; j++) {
				System.out.print(a + j + "\t");
			}
			System.out.print("\n");
			a++;
		}

	}

}
